section.source:
enum Shapes {
  SQUARE, TRIANGLE, CIRCLE, OVAL
}

enum Shapes {
  SQUARE, TRIANGLE, CIRCLE
} = CIRCLE;

record Employee {
  string name;
  boolean active = true;
  long salary;
}

error Kaboom {
  string explanation;
  int result_code = -1;
}

record Job {
  string jobid;
  date submitDate;
  time_ms submitTime;
  timestamp_ms finishTime;
  decimal(9,2) finishRatio;
}

record Card {
  Suit suit; // refers to the enum Card defined above
  int number;
}

record RecordWithUnion {
  union { null, string } optionalString;
}

section.p:
All Java-style comments are supported within a Avro IDL file. Any text following
        // on a line is ignored, as is any text between /* and
        */, possibly spanning multiple lines.

section.source:
record MyRecord {
  string @order("ascending") myAscendingSortField;
  string @order("descending")  myDescendingField;
  string @order("ignore") myIgnoredField;
}

record MyRecord {
  @java-class("java.math.BigDecimal") string value;
  @java-key-class("java.io.File") map<string> fileStates;
  array<@java-class("java.math.BigDecimal") string> weights;
}

@namespace("org.apache.avro.firstNamespace")
protocol MyProto {
  @namespace("org.apache.avro.someOtherNamespace")
  record Foo {}

  record Bar {}
}

/**
 * An example protocol in Avro IDL
 */
@namespace("org.apache.avro.test")
protocol Simple {

  @aliases(["org.foo.KindOf"])
  enum Kind {
    FOO,
    BAR, // the bar enum value
    BAZ
  }

  fixed MD5(16);

  record TestRecord {
    @order("ignore")
    string name;

    @order("descending")
    Kind kind;

    MD5 hash;

    union { MD5, null} @aliases(["hash"]) nullableHash;

    array<long> arrayOfLongs;
  }

  error TestError {
    string message;
  }

  string hello(string greeting);
  TestRecord echo(TestRecord `record`);
  int add(int arg1, int arg2);
  bytes echoBytes(bytes data);
  void `error`() throws TestError;
  void ping() oneway;
}
